using StdLib;
using static StdLib.DerivedSignal;

class Script
{
    const string Folder = "Calculated";

    DerivedSignal Sum0102 = new(
        parentFolderID: Folder,
        unit: "kW",
        resolution: Duration.FromMinutes(1),
        inputsRequired: InputsRequired.None,
        inputs: [
            DefineInput("a", DataItemRef("Data_001")),
            DefineInput("b", DataItemRef("Data_002")),
        ],
        calculation: (double a, double b) => {
            //a = double.IsNaN(a) ? 10.0 : a;
            //b = double.IsNaN(b) ? 10.0 : b;
            return a + b;
        }
    );

    public void Step(Timestamp t, Duration dt) {
        //Console.WriteLine("Calculations...");
        UpdateDerivedSignals(this, t);
        //Console.WriteLine("Calculations end..");
    }
}

using Std;

class Script {

    Input  A = new Input(name: "A", unit: "mg/L", defaultValue: null);
    Input  B = new Input(name: "B", unit: "mg/L", defaultValue: null);
    //Input  C = new Input(name: "C", unit: "mg/L", defaultValue: null);

    Output Y = new Output("Y", "m³/d");

    public void Step(Timestamp t, Duration dt) {

        if (A.HasValidValue&& B.HasValidValue /* && C.HasValidValue*/) {
            Y.Value = A.Value + B.Value; // + C.Value;
        }
    }
}
